    @Test
    public void testUpsertSuccess() {
        doReturn(assetInfoDTO).when(collateralClient)
                .callApiCommonAsset(isA(Object.class), isA(HttpMethod.class), isA(String.class), eq(AssetResponse.class));
        when(collateralClient.callApiCommonAsset(any(), any(), any(), eq(AssetRuleChecklistDataResponse.class)))
                .thenReturn(ruleChecklistDataResponse);
        when(checklistService.uploadFileChecklist(any()))
                .thenReturn(dataUploadChecklist);
        when(checklistService.reloadChecklistAsset(any())).thenReturn(checklists);
        when(objectMapper.convertValue(any(), eq(AssetCommonInfoDTO.class)))
                .thenReturn(assetInfo);

        assertDoesNotThrow(() -> assetInfoService.upsertAsset(request));
    }

    @Test
    public void testUpsertInvalidRequest() {
        doReturn(null).when(collateralClient)
                .callApiCommonAsset(isA(Object.class), isA(HttpMethod.class), isA(String.class), eq(AssetResponse.class));
        assertThrows(ApprovalException.class, () -> assetInfoService.upsertAsset(request));
    }

    @Test
    public void testUpsertReloadChecklistFail() {
        doReturn(assetInfoDTO).when(collateralClient)
                .callApiCommonAsset(isA(Object.class), isA(HttpMethod.class), isA(String.class), eq(AssetResponse.class));
        when(collateralClient.callApiCommonAsset(any(), any(), any(), eq(AssetRuleChecklistDataResponse.class)))
                .thenReturn(ruleChecklistDataResponse);
        when(checklistService.uploadFileChecklist(any()))
                .thenReturn(null);
        when(checklistService.reloadChecklistAsset(any())).thenReturn(checklists);

        assertThrows(ApprovalException.class, () -> assetInfoService.upsertAsset(request));
    }

